// import { useState } from 'react'
import React, {useState}from 'react'
import { NavLink } from 'react-router-dom'
import {useHistory} from 'react-router';

function Register() {
  const history= useHistory();
  const [user,setUser]=useState({
    username:"",
    email:"",
    password:""
  })

  //handle input

  const handleInput=(event)=>{
    let name=event.target.name;
    let value=event.target.value;

    setUser({...user,[name]:value});                                                              
  }
  //hendle submit
const handleSubmit =async (event)=>{
  event.preventDefault();
  //store object data into variable
  const {username,email,password}=user;
  try {
    const res=await fetch('/register',{
      method:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        username,email,password
      })
    })
    if(res.status===400|| !res){
      window.alert("already used detail")
    }else{
      window.alert("registered successfully");
      history.pushState('/login')
    }
  } catch (error) {
    console.log(error);
  }
}


  return (
    <div>
      <div className='container shadow my-5' >
            <div className='row'>
                <div className='col-md-5 d-flex flex-column align-item-center form'>
                    <h1 className='display-4 fw-bolder'> welcome register</h1>
                    <p className="lead text-center">Enter your crenditials</p>
                    <h5>
                      or
                    </h5>
                    <NavLink to="/register" className="btn btn-outline-light rounded-pill pb-2 w-50">Register</NavLink>

                </div>
                <div className='col-md-6'><h1 className='display-6' > Login</h1>
                <form onSubmit={handleSubmit} method="POST">
  <div class="form-group">
    <label for="exampleInputEmail1">name</label>
    <input type="text" class="form-control" id="name" name="username" value={user.username} onChange={handleInput} />
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" name="email" value={user.email} onChange={handleInput} />
    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control" id="exampleInputEmail1" name="password" value={user.password} onChange={handleInput} />
    
  </div>
  <div class="form-group form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form></div>
            </div>
        </div>
    </div>
  )
}

export default Register
