// import { useState } from 'react'
import { application } from 'express';
import React, {useState}from 'react'
import { NavLink } from 'react-router-dom'
// import {useHistory} from 'react-router';

function Application() {

    const [appl,setAppl]=useState({
        name:"",
        email:"",
        surname:""
      })
    
      //handle input
    
      const handleChange=(event)=>{
        let name=event.target.name;
        let value=event.target.value;
    
        setAppl({...appl,[name]:value});                                                              
      }
      //hendle submit
    const handleSubmit =async (event)=>{
      event.preventDefault();
      //store object data into variable
      const {name,email,surname}=appl;
      try {
        const res=await fetch('/application',{
          method:"POST",
          headers:{
            "Content-Type":"application/json"
          },
          body:JSON.stringify({
            name,email,surname
          })
        })
        if(res.status === 400 || !res){
          window.alert("application not submited yet ! please try again later")
        }else{
          window.alert("application submited");
          setAppl({name:'',
                  email:'',
                  surname:''})
          // history.pushState('/login')  
        }
      } catch (error) {
        console.log(error);
      }
}
return(
    <div>
 <div className='container shadow my-5' >
            <div className='row'>
                <div className='col-md-5 d-flex flex-column align-item-center form'>
                    <h1 className='display-4 fw-bolder'> welcome register</h1>
                    <p className="lead text-center">Enter your crenditials</p>
                    <h5>
                      or
                    </h5>
                    <NavLink to="/register" className="btn btn-outline-light rounded-pill pb-2 w-50">Register</NavLink>

                </div>
                <div className='col-md-6'><h1 className='display-6' > Login</h1>
                <form onSubmit={handleSubmit} method="POST">
  <div className="form-group">
    <label for="exampleInputEmail1">name</label>
    <input type="text" className="form-control" id="name" name="name" value={appl.name} onChange={handleChange} />
    {/* <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small> */}
  </div>
  <div className="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" name="email" value={appl.email} onChange={handleChange} />
    {/* <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small> */}
  </div>
  <div className="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="text" className="form-control" id="exampleInputEmail1" name="surname" value={appl.surname} onChange={handleChange} />
    
  </div>
  <div className="form-group form-check">
    <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
    <label className="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" className="btn btn-primary">Submit</button>
</form></div>
            </div>
        </div>
    </div>

)
}
export default Application;
